INSERT INTO post(title, content) VALUES ('Hvordan har Barbies Drømmehus ændret sig gennem årene?', 'Barbies Drømmehus har været et ikonisk element i mange børns legetid. Fra dets tidlige design til de moderne versioner i dag, hvordan har det udviklet sig med hensyn til design, funktioner og teknologi?');
INSERT INTO comment(author, content, post_id) VALUES ('Raquelle', 'det har nu mere avancerede funktioner som en fungerende elevator og lyseffekter', 1), ('Christie', 'det første Drømmehus var faktisk lavet af pap i 1962, og det nuværende har interaktive funktioner', 1), ('Summer', 'husets design har ændret sig for at afspejle arkitektoniske trends gennem årene', 1);
INSERT INTO post(title, content) VALUES ('Hvilket job har Barbie ikke haft endnu?', 'Gennem årene har Barbie udforsket mange forskellige professioner, fra at være læge til astronaut. Men er der nogle job, hun endnu ikke har prøvet?');
INSERT INTO comment(author, content, post_id) VALUES ('Stacie', 'hun har dækket mange områder, men jeg har ikke set hende som en kryptolog endnu', 2), ('Teresa', 'selvom hun har været astronaut, har hun endnu ikke været dybhavsforsker', 2), ('Skipper', 'jeg tror ikke, hun har været en dronepilot endnu', 2), ('Renee', 'selvom hun har haft over 200 job, har hun endnu ikke prøvet rollen som en softwareudvikler', 2);
INSERT INTO post(title, content) VALUES ('Hvem er Barbies ældste ven?', 'Barbie har en bred cirkel af venner, fra Midge til Teresa. Men hvem har været ved hendes side fra begyndelsen?');
INSERT INTO comment(author, content, post_id) VALUES ('Nikki', 'Midge, introduceret i 1963, var Barbies første ven og er stadig en af hendes nærmeste venner', 3), ('Midge', 'Christie, som blev introduceret i 1968, var Barbies første sorte veninde', 3), ('Renee', 'Ken har været ved hendes side siden 1961 og er en af hendes ældste venner', 3);
INSERT INTO post(title, content) VALUES ('Hvilken sport dyrker Barbie mest i sin fritid?', 'Ud over hendes mange professioner har Barbie også haft tid til at dyrke sport. Hvilken sport har hun mest passion for?');
INSERT INTO comment(author, content, post_id) VALUES ('Teresa', 'ridning er en anden sport, hun nyder, især med hendes hest, Tawny', 4), ('Summer', 'hun har også deltaget i mange svømmekonkurrencer gennem årene', 4), ('Ken', 'hun har en stor passion for gymnastik, som hun ofte deltager i', 4);
INSERT INTO post(title, content) VALUES ('Hvordan mødte Barbie og Ken hinanden?', 'Barbie og Ken har været et ikonisk par i mange årtier. Men hvordan begyndte deres eventyr sammen?');
INSERT INTO comment(author, content, post_id) VALUES ('Christie', 'de mødtes på en TV-reklame i 1961, og siden da har de været uadskillelige', 5), ('Raquelle', 'deres første møde var på en modevisning, hvor de begge var modeller', 5), ('Christie', 'de blev introduceret til hinanden af en fælles ven på en fest i Malibu', 5);
INSERT INTO post(title, content) VALUES ('Hvordan holder Barbie sig opdateret med de nyeste modetrends?', 'Mode har altid været en stor del af Barbies verden. Hvordan holder hun sig opdateret med de seneste trends?');
INSERT INTO comment(author, content, post_id) VALUES ('Skipper', 'hun deltager regelmæssigt i modeuger rundt om i verden for at få inspiration', 6), ('Midge', 'hun samarbejder med top designere for at sikre, at hun altid er i trend', 6), ('Skipper', 'hun har også en passion for modejournalistik og læser mange modeblade', 6);
INSERT INTO post(title, content) VALUES ('Hvilket kæledyr er Barbies favorit?', 'Fra hunde til katte og endda heste, Barbie har haft mange kæledyr. Men hvilket kæledyr holder hun mest af?');
INSERT INTO comment(author, content, post_id) VALUES ('Skipper', 'hun har en særlig forkærlighed for sin hund, Honey, som altid er ved hendes side', 7), ('Midge', 'hun har en stærk forbindelse med sin hest, Tawny, som hun ofte rider på', 7);
INSERT INTO post(title, content) VALUES ('Hvor mange outfits ejer Barbie?', 'Barbies garderobe er legendarisk. Men hvor mange outfits har hun egentlig?');
INSERT INTO comment(author, content, post_id) VALUES ('Stacie', 'det nøjagtige antal er ukendt, men det anslås, at hun har over 10.000 outfits', 8), ('Midge', 'hun har også mange sko, med over 1.000 par i hendes samling', 8), ('Summer', 'hun har en garderobe, der kan matche enhver lejlighed, fra dykkerdragter til gallakjoler', 8);
INSERT INTO post(title, content) VALUES ('Hvordan forbereder Barbie sig til en stranddag?', 'En dag ved stranden er altid sjovt med Barbie. Hvordan forbereder hun sig på en solrig dag ved havet?');
INSERT INTO comment(author, content, post_id) VALUES ('Summer', 'hun pakker også altid en god bog og nogle snacks', 9), ('Nikki', 'ud over de sædvanlige strandting som solcreme og håndklæder, har hun altid en chic strandtaske', 9);
INSERT INTO post(title, content) VALUES ('Hvilke hobbyer nyder Barbie mest?', 'Ud over hendes mange job og eventyr, hvad gør Barbie i hendes fritid? Har hun nogen hobbyer?');
INSERT INTO comment(author, content, post_id) VALUES ('Stacie', 'musik er en anden passion, og hun spiller guitar og synger', 10), ('Christie', 'hun nyder også fotografering og tager ofte billeder af sine eventyr', 10);